mapper.update:

/* StudyAchieve.uptData */
UPDATE tb_datasales
SET
    state = #{state}
  ,reason = #{reason}
WHERE sid = #{sid}

mapper.select:
/* StudyAchieve.getDataSaleList */ SELECT sid ,studynm ,sosuk ,readuser ,DATE_FORMAT(sdt, '%Y-%m-%d') AS sdt ,DATE_FORMAT(regDt, '%Y-%m-%d') AS regDt ,CASE WHEN state = '2' THEN '접수' WHEN state = '3' THEN '승인' WHEN state = '4' THEN '반려' ELSE '신청' END AS state FROM tb_datasales WHERE 1 = 1
AND ( studynm LIKE CONCAT('%', #{sSubject}, '%') OR purpose LIKE CONCAT('%', #{sSubject}, '%') OR cont LIKE CONCAT('%', #{sSubject}, '%') )
AND regid = #{regid}
AND state = #{sState}
ORDER BY regDt DESC
LIMIT #{offSet}, #{limit}


/* StudyAchieve.getDataSaleCnt */
SELECT count(*) as cnt
FROM tb_datasales
WHERE 1 = 1


    AND (
    studynm LIKE CONCAT('%', #{sSubject}, '%')
    OR
    purpose LIKE CONCAT('%', #{sSubject}, '%')
    OR
    cont LIKE CONCAT('%', #{sSubject}, '%')
    )


    AND regid = #{regid}


    AND state = #{sState}

mapper.insert:

/* StudyAchieve.insDataSale */
INSERT INTO tb_datasales
(
     sid
    ,studynm
    ,sosuk
    ,readuser
    ,partuser
    ,sdt
    ,edt
    ,purpose
    ,cont
    ,regid
    ,regdt
    ,itemlist
)
VALUES
(
     #{sid}
    ,#{studynm}
    ,#{sosuk}
    ,#{readuser}
    ,#{partuser}
    ,#{sdt}
    ,#{edt}
    ,#{purpose}
    ,#{cont}
    ,#{regid}
    ,now()
    ,#{itemlist}
)


/* StudyAchieve.insData */
INSERT INTO tb_study_achieve
(
     bid
    ,gubun
    ,subject
    ,cont
    ,summary
    ,regdt
)
VALUES
(
     #{bId}
    ,#{gubun}
    ,#{subject}
    ,#{cont}
    ,#{summary}
    ,now()
)

mapper.update:

/* StudyAchieve.uptData */
UPDATE tb_study_achieve
SET
   gubun = #{gubun}
  ,subject = #{subject}
  ,cont = #{cont}
  ,summary = #{summary}
  ,moddt = now()
WHERE bid = #{bId}

mapper.delete:

/* StudyAchieve.delData */
DELETE FROM tb_study_achieve
WHERE bid = #{bId}

mapper.select:
/* StudyAchieve.getDataCnt */ SELECT count(*) as cnt FROM tb_study_achieve WHERE 1 = 1
AND ( subject LIKE CONCAT('%', #{sSubject}, '%') OR cont LIKE CONCAT('%', #{sSubject}, '%') )
AND DATE_FORMAT(regDt, '%Y%m%d') BETWEEN DATE_FORMAT(IFNULL(NULLIF(#{sRegDt},''),'00000000'), '%Y%m%d') AND DATE_FORMAT(IFNULL(NULLIF(#{eRegDt},''),'99991231'), '%Y%m%d')

/* StudyAchieve.getDataList */ SELECT bid ,gubun ,subject ,cont ,summary ,DATE_FORMAT(regDt, '%Y-%m-%d') AS regDt FROM tb_study_achieve WHERE 1 = 1
AND ( subject LIKE CONCAT('%', #{sSubject}, '%') OR cont LIKE CONCAT('%', #{sSubject}, '%') )
AND DATE_FORMAT(regDt, '%Y%m%d') BETWEEN DATE_FORMAT(IFNULL(NULLIF(#{sRegDt},''),'00000000'), '%Y%m%d') AND DATE_FORMAT(IFNULL(NULLIF(#{eRegDt},''),'99991231'), '%Y%m%d')
ORDER BY regDt DESC
LIMIT #{offSet}, #{limit}
