mapper.insert:

/* Users.insData */
INSERT INTO tb_members
(
     uid
    ,userid
    ,userpw
    ,usernm
    ,sosuk
    ,jobnm
    ,email
    ,regdt
)
VALUES
(
     #{uid}
    ,#{userid}
    ,#{userpw}
    ,#{usernm}
    ,#{sosuk}
    ,#{jobnm}
    ,#{email}
    ,now()
)

mapper.update:

/* Users.uptData */
UPDATE tb_members
SET
     usernm	= #{usernm}
    ,sosuk	= #{sosuk}
    ,jobnm	= #{jobnm}
    ,email	= #{email}
    ,userlv	= #{userlv}
    ,useyn	= #{useyn}
WHERE uid = #{uid}


/* Users.uptData */
UPDATE tb_members
SET
     userpw	= #{userpw}
    ,useyn	= #{useyn}
WHERE uid = #{uid}

mapper.delete:

/* Users.delData */
DELETE FROM tb_members
WHERE uid = #{uid}

mapper.select:
/* Users.getDataCnt */ SELECT count(*) as cnt FROM tb_members WHERE 1 = 1
AND usernm LIKE CONCAT('%', #{sUsernm}, '%')
AND DATE_FORMAT(regDt, '%Y%m%d') BETWEEN DATE_FORMAT(IFNULL(NULLIF(#{sRegDt},''),'00000000'), '%Y%m%d') AND DATE_FORMAT(IFNULL(NULLIF(#{eRegDt},''),'99991231'), '%Y%m%d')

/* Users.getDataList */ SELECT uid ,userid ,userpw ,usernm ,sosuk ,jobnm ,email ,userlv ,useyn ,DATE_FORMAT(regDt, '%Y-%m-%d') AS regDt FROM tb_members WHERE 1 = 1
AND usernm LIKE CONCAT('%', #{sUsernm}, '%')
AND DATE_FORMAT(regDt, '%Y%m%d') BETWEEN DATE_FORMAT(IFNULL(NULLIF(#{sRegDt},''),'00000000'), '%Y%m%d') AND DATE_FORMAT(IFNULL(NULLIF(#{eRegDt},''),'99991231'), '%Y%m%d')
ORDER BY regDt DESC
LIMIT #{offSet}, #{limit}
